document.addEventListener("DOMContentLoaded",function(){const t=document.querySelector(".hamburger"),n=document.querySelector(".sidebar");t&&n&&t.addEventListener("click",function(){n.classList.toggle("hidden")}),gsap.fromTo(".hero-arrow",{opacity:0,y:-10},{opacity:1,y:0,repeat:-1,yoyo:!0,duration:1});const e=document.querySelector(".btn-hire-me");e&&(e.addEventListener("mouseenter",()=>{gsap.to(e,{backgroundColor:"#B8A58E",color:"#fff",scale:1.1,duration:.3,ease:"power3.inOut"})}),e.addEventListener("mouseleave",()=>{gsap.to(e,{backgroundColor:"transparent",color:"#00bcd4",scale:1,duration:.3,ease:"power3.inOut"})}));const s=document.querySelector(".color-mode-toggle");s&&s.addEventListener("click",()=>{document.body.classList.toggle("colorscheme-light"),document.body.classList.toggle("colorscheme-dark")})})